import React from "react";
// MUI
import { makeStyles, useTheme } from "@material-ui/core/styles";
import Grid from "@material-ui/core/Grid";
import  Typography  from "@material-ui/core/Typography";
import { useMediaQuery } from "@material-ui/core";
import CardMedia from '@material-ui/core/CardMedia';
import P1 from "./../assets/Marvel.mov"
import P3 from "./../assets/magic.mov"
import P5 from "./../assets/tesla.mov"

export default function Projects() {
	const classes = styles();
	const theme = useTheme();
    // media queries
    const matchesSM = useMediaQuery(theme.breakpoints.down("sm"))
    const matchesXS = useMediaQuery(theme.breakpoints.down("xs")) 

	return (
		<React.Fragment>
			<Grid
            id="about"
            className={classes.aboutMeContainer}
            container
            justify="space-around"
            alignItems="center"
            direction="row"
            >
                <Grid item>
                    <Typography variant="h2" align={matchesSM ? "center" : "left"}>About Me</Typography>
                    <Typography variant="body1" align={matchesSM ? "center" : "left"} className={classes.aboutMeBody}>
                    Take a look at some of my projects. 
                    </Typography>
                </Grid>
                <CardMedia
            component='video'
            className={classes.media}
            image={P1}
            autoPlay
        />
        <CardMedia
            component='video'
            className={classes.media}
            image={P3}
            autoPlay
        />
        <CardMedia
            component='video'
            className={classes.media}
            image={P5}
            autoPlay
        />
            </Grid>
		</React.Fragment>
	);
}
