import {createMuiTheme} from "@material-ui/core/styles";

// Palette
const myBlue = "#227093";
const myGreen = "#2A9D8F";

export default createMuiTheme({
    palette: {
        common: { blue: `${myBlue}`, green: `${myGreen}`},
        primary: { main: `${myBlue}`},
        secondary: { main: `${myGreen}`},
    }, 
    typography: {
        p: {}, 
        h1: {},
        body1: {
            maxWidth: "45ch",
        },
    },
    button: {
        marginLeft: "2.5rem",
        backgroundColor: myBlue,
        color: "#fff",
        borderRadius: "30px",
        height: "30px",
        padding: "15px",
        fontSize: "1rem",
    }
})
