import React from "react";
// MUI
import { makeStyles, useTheme } from "@material-ui/core/styles";
import Grid from "@material-ui/core/Grid";
import  Typography  from "@material-ui/core/Typography";
import { useMediaQuery } from "@material-ui/core";
import CardMedia from '@material-ui/core/CardMedia';
import P1 from "./../assets/Marvel.mov"
import P3 from "./../assets/magic.mov"
import P5 from "./../assets/tesla.mov"
// 


const styles = makeStyles ((theme) => ({
    media1: {
        width: "20rem",
    }, 
    media2: {
        width: "30rem",
    }, 
    media3: {
        width: "30rem",
    }


}));

export default function Projects() {
	const classes = styles();
	const theme = useTheme();
    // media queries
    const matchesSM = useMediaQuery(theme.breakpoints.down("sm"))

	return (
		<React.Fragment>
			<Grid
            id="about"
            className={classes.aboutMeContainer}
            container
            justify="space-around"
            alignItems="center"
            direction="row"
            >
                <Grid item>
                    <Typography variant="body1" align={matchesSM ? "center" : "left"}>
                    Take a look at some of my projects. 
                    </Typography>
                </Grid>
                <CardMedia
            component='video'
            className={classes.media1}
            src={P1}
            autoPlay
        />
        <CardMedia
            component='video'
            className={classes.media2}
            src={P3}
            autoPlay
        />
        <CardMedia
            component='video'
            className={classes.media3}
            src={P5}
            autoPlay
        />
            </Grid>
		</React.Fragment>
	);
}
