import {createMuiTheme} from "@material-ui/core/styles";

const myBlue = "#227093";
const myGreen = "#2A9D8F";

export default createMuiTheme({
    palette: {
        common: { blue: `${myBlue}`, green: `${myGreen}`},
        primary: { main: `${myBlue}`},
        secondary: { main: `${myGreen}`},
    }, 
    typography: {
        p: {}, 
        h1: {},
    },
    button: {
        marginLeft: "2.5rem",
        backgroundColor: myBlue,
        color: "#fff",
        borderRadius: "30px",
        height: "30px",
        padding: "15px",

    }
})
